package author;

public class Author {
String name,place;
int age;
	public Author(String name,String place, int age) {
		// TODO Auto-generated constructor stub
		this.name=name;
		this.place=place;
		this.age=age;
	}
	public String getName() {
		return name;
	}
	
	public String getPlace() {
		return place;
	}
	
	public int getAge() {
		return age;
	}
	
	
}
